// tambah ini ke constructor
private userData: UserService,

// tambah ini ke constructor
this.loadUserData();

// Ini bisa di constructor atau di ngOnInit atau buat initApps trus taro di constructor
loadUserData() {
    this.userData.user$.subscribe((state) => {
        if (state.data) {
            this.NamaPolisi = state.data.fullname;
            this.NamaPangkatTerakhir = state.data.last_rank_name;
            this.NRP = state.data.nrp;
            this.Jabatan = state.data.last_position_name;
            this.Operator = state.data.username;
            this.kodeSatuan = state.data.last_sipp_unit_code;
            this.namaSatuan = state.data.last_unit_hierarchy;
        }
    });
    this.userData.loadUser();
}
